<script>
export default {
  name: "Call",
  props: {
    companyName: { type: String },
  },
  data() {
    return {
      mute: false,
      digits: "",
    };
  },
  methods: {
    changeState(state) {
      this.$emit("ChangeState", state);
    },
    changeSubState(subState, state) {
      this.$emit("ChangeSubState", subState, state);
    },
    updateSubState(subState, state) {
      switch (subState) {
        case "digitPressState":
          this.digitPressState(state);
          break;
        case "muteState":
          this.updateMmute(state);
          break;

        default:
          break;
      }
    },
    updateMmute: function (mute) {
      console.log("Call update MuteState ", mute);
      this.mute = mute;
    },
    changeMute() {
      this.changeSubState("muteState", !this.mute);
    },
    sendDTMF(digit) {
      this.changeSubState("digitPressState", digit);
    },
    digitPressState: function (digit) {
      this.digits += digit;
      console.log(`Call Add digit ${digit}, digits ${this.digits}`);
    },
  },
};
</script>